mapper.delete:
delete from
t_token_tracker
where tokenid=#{tokenid} and subtokenid=#{subtokenid}

mapper.update:
update t_token_tracker
creator = #{creator}, owner = #{owner}, createtime = #{createtime}, lastupdatetime = #{lastupdatetime}, hash = #{hash},
where tokenid = #{tokenid} and subtokenid=#{subtokenid}
